# Create comprehensive migration assessment

echo "Creating migration assessment framework..."

# Create assessment script for on-premises infrastructure
cat > migration-assessment-script.ps1 << 'EOF'
# PowerShell script for comprehensive migration assessment
# Run this script on target servers for migration assessment

param(
    [string]$OutputPath = "C:\MigrationAssessment",
    [string]$ServerName = $env:COMPUTERNAME
)

# Create output directory
if (!(Test-Path $OutputPath)) {
    New-Item -Path $OutputPath -ItemType Directory -Force
}

# System Information Collection
Write-Host "Collecting system information..."
$SystemInfo = @{
    ServerName = $ServerName
    OS = (Get-WmiObject Win32_OperatingSystem).Caption
    OSVersion = (Get-WmiObject Win32_OperatingSystem).Version
    Architecture = (Get-WmiObject Win32_OperatingSystem).OSArchitecture
    TotalRAM = [math]::Round((Get-WmiObject Win32_ComputerSystem).TotalPhysicalMemory / 1GB, 2)
    CPUCores = (Get-WmiObject Win32_Processor | Measure-Object NumberOfCores -Sum).Sum
    LogicalProcessors = (Get-WmiObject Win32_Processor | Measure-Object NumberOfLogicalProcessors -Sum).Sum
}

# Disk Information
Write-Host "Collecting disk information..."
$Disks = Get-WmiObject Win32_LogicalDisk | Where-Object { $_.DriveType -eq 3 } | ForEach-Object {
    @{
        Drive = $_.DeviceID
        SizeGB = [math]::Round($_.Size / 1GB, 2)
        FreeSpaceGB = [math]::Round($_.FreeSpace / 1GB, 2)
        UsedSpaceGB = [math]::Round(($_.Size - $_.FreeSpace) / 1GB, 2)
        UsagePercent = [math]::Round((($_.Size - $_.FreeSpace) / $_.Size) * 100, 1)
    }
}

# Network Configuration
Write-Host "Collecting network information..."
$NetworkAdapters = Get-WmiObject Win32_NetworkAdapterConfiguration | Where-Object { $_.IPEnabled } | ForEach-Object {
    @{
        Description = $_.Description
        IPAddress = $_.IPAddress -join ", "
        SubnetMask = $_.IPSubnet -join ", "
        DefaultGateway = $_.DefaultIPGateway -join ", "
        DNSServers = $_.DNSServerSearchOrder -join ", "
        MACAddress = $_.MACAddress
    }
}

# Installed Software
Write-Host "Collecting installed software..."
$InstalledSoftware = Get-WmiObject Win32_Product | Select-Object Name, Version, Vendor | Sort-Object Name

# Windows Services
Write-Host "Collecting Windows services..."
$Services = Get-Service | Where-Object { $_.StartType -ne "Disabled" } | Select-Object Name, Status, StartType, DisplayName

# Performance Counters (sample)
Write-Host "Collecting performance data..."
$PerformanceData = @{
    CPUUtilization = (Get-Counter "\Processor(_Total)\% Processor Time").CounterSamples.CookedValue
    MemoryUtilization = [math]::Round(((Get-Counter "\Memory\Available MBytes").CounterSamples.CookedValue / ($SystemInfo.TotalRAM * 1024)) * 100, 2)
    DiskIOPS = (Get-Counter "\PhysicalDisk(_Total)\Disk Transfers/sec").CounterSamples.CookedValue
}

# SQL Server Detection (if applicable)
Write-Host "Checking for SQL Server..."
$SQLInstances = @()
try {
    $SQLServices = Get-Service | Where-Object { $_.Name -like "MSSQL*" -or $_.Name -like "SQLServer*" }
    if ($SQLServices) {
        $SQLInstances = $SQLServices | ForEach-Object {
            @{
                ServiceName = $_.Name
                DisplayName = $_.DisplayName
                Status = $_.Status
                StartType = $_.StartType
            }
        }
    }
} catch {
    Write-Host "SQL Server detection failed: $($_.Exception.Message)"
}

# IIS Detection
Write-Host "Checking for IIS..."
$IISInfo = @{}
try {
    $IISFeature = Get-WindowsFeature -Name "IIS-WebServerRole" -ErrorAction SilentlyContinue
    if ($IISFeature -and $IISFeature.InstallState -eq "Installed") {
        Import-Module WebAdministration -ErrorAction SilentlyContinue
        $IISInfo = @{
            Installed = $true
            Version = (Get-ItemProperty "HKLM:\SOFTWARE\Microsoft\InetStp" -Name "MajorVersion").MajorVersion
            Sites = @(Get-Website | Select-Object Name, State, PhysicalPath, @{Name="Bindings"; Expression={$_.Bindings.Collection | ForEach-Object {"$($_.protocol):$($_.bindingInformation)"}}})
            ApplicationPools = @(Get-IISAppPool | Select-Object Name, State, ProcessModel)
        }
    } else {
        $IISInfo = @{ Installed = $false }
    }
} catch {
    $IISInfo = @{ Installed = $false, Error = $_.Exception.Message }
}

# Compile assessment report
$AssessmentReport = @{
    Timestamp = Get-Date -Format "yyyy-MM-dd HH:mm:ss"
    SystemInfo = $SystemInfo
    Disks = $Disks
    NetworkAdapters = $NetworkAdapters
    InstalledSoftware = $InstalledSoftware
    Services = $Services
    PerformanceData = $PerformanceData
    SQLInstances = $SQLInstances
    IISInfo = $IISInfo
}

# Export to JSON
$OutputFile = Join-Path $OutputPath "$ServerName-Assessment-$(Get-Date -Format 'yyyyMMdd-HHmmss').json"
$AssessmentReport | ConvertTo-Json -Depth 5 | Out-File -FilePath $OutputFile -Encoding UTF8

Write-Host "Assessment completed. Report saved to: $OutputFile"

# Create CSV summary for easy analysis
$SummaryFile = Join-Path $OutputPath "$ServerName-Summary-$(Get-Date -Format 'yyyyMMdd-HHmmss').csv"
$Summary = [PSCustomObject]@{
    ServerName = $SystemInfo.ServerName
    OS = $SystemInfo.OS
    CPUCores = $SystemInfo.CPUCores
    RAM_GB = $SystemInfo.TotalRAM
    TotalDiskSpace_GB = ($Disks | Measure-Object SizeGB -Sum).Sum
    UsedDiskSpace_GB = ($Disks | Measure-Object UsedSpaceGB -Sum).Sum
    NetworkAdapters = $NetworkAdapters.Count
    InstalledSoftware = $InstalledSoftware.Count
    SQLInstalled = ($SQLInstances.Count -gt 0)
    IISInstalled = $IISInfo.Installed
    AssessmentDate = (Get-Date -Format "yyyy-MM-dd HH:mm:ss")
}

$Summary | Export-Csv -Path $SummaryFile -NoTypeInformation

Write-Host "Summary exported to: $SummaryFile"
EOF

# Create migration readiness checker
cat > check-migration-readiness.py << 'EOF'
#!/usr/bin/env python3
"""
Migration Readiness Checker
Analyzes assessment data and provides migration recommendations
"""

import json
import csv
import sys
import os
from datetime import datetime

class MigrationReadinessChecker:
    def __init__(self):
        self.readiness_score = 0
        self.recommendations = []
        self.blocking_issues = []
        self.warnings = []

    def analyze_assessment(self, assessment_file):
        """Analyze assessment data and provide recommendations"""
        try:
            with open(assessment_file, 'r') as f:
                data = json.load(f)

            self._check_os_compatibility(data)
            self._check_hardware_requirements(data)
            self._check_software_compatibility(data)
            self._check_network_configuration(data)
            self._check_database_migration(data)
            self._check_web_server_migration(data)

            return self._generate_report(data)

        except Exception as e:
            return f"Error analyzing assessment: {str(e)}"

    def _check_os_compatibility(self, data):
        """Check OS compatibility with Azure"""
        os_info = data.get('SystemInfo', {})
        os_name = os_info.get('OS', '')

        if 'Windows Server 2008' in os_name:
            self.blocking_issues.append("Windows Server 2008 is end-of-life. Upgrade required before migration.")
            self.readiness_score -= 30
        elif 'Windows Server 2012' in os_name:
            self.warnings.append("Windows Server 2012 support ends soon. Consider upgrading.")
            self.readiness_score -= 10
        elif any(version in os_name for version in ['2016', '2019', '2022']):
            self.readiness_score += 20
            self.recommendations.append("OS is compatible with Azure. Consider Azure Hybrid Benefit for licensing.")

    def _check_hardware_requirements(self, data):
        """Check if hardware meets Azure VM requirements"""
        system_info = data.get('SystemInfo', {})
        ram_gb = system_info.get('TotalRAM', 0)
        cpu_cores = system_info.get('CPUCores', 0)

        if ram_gb < 1:
            self.blocking_issues.append("Insufficient RAM. Minimum 1GB required for Azure VMs.")
            self.readiness_score -= 25
        elif ram_gb < 4:
            self.warnings.append("Low RAM detected. Consider upgrading for better performance.")
            self.readiness_score -= 5
        else:
            self.readiness_score += 15

        if cpu_cores < 1:
            self.blocking_issues.append("Insufficient CPU cores. Minimum 1 core required.")
            self.readiness_score -= 25
        else:
            self.readiness_score += 10

        # Recommend Azure VM sizes based on resources
        if ram_gb <= 8 and cpu_cores <= 4:
            self.recommendations.append("Recommended Azure VM size: Standard_D2s_v3 or Standard_B2s")
        elif ram_gb <= 16 and cpu_cores <= 8:
            self.recommendations.append("Recommended Azure VM size: Standard_D4s_v3")
        else:
            self.recommendations.append("Consider higher-tier Azure VM sizes for optimal performance")

    def _check_software_compatibility(self, data):
        """Check installed software compatibility"""
        software_list = data.get('InstalledSoftware', [])

        legacy_software = []
        for software in software_list:
            name = software.get('Name', '').lower()
            if any(legacy in name for legacy in ['norton', 'mcafee', 'trend micro']):
                legacy_software.append(software.get('Name'))

        if legacy_software:
            self.warnings.append(f"Legacy security software detected: {', '.join(legacy_software)}. Consider Azure Security Center.")
            self.readiness_score -= 5

        self.readiness_score += min(len(software_list) * 2, 20)  # Cap at 20 points

    def _check_network_configuration(self, data):
        """Check network configuration"""
        network_adapters = data.get('NetworkAdapters', [])

        if not network_adapters:
            self.blocking_issues.append("No active network adapters detected.")
            self.readiness_score -= 30
        else:
            self.readiness_score += 10
            self.recommendations.append("Configure Azure Virtual Network for optimal connectivity.")

    def _check_database_migration(self, data):
        """Check database migration requirements"""
        sql_instances = data.get('SQLInstances', [])

        if sql_instances:
            self.recommendations.append("SQL Server detected. Consider Azure SQL Database or SQL Managed Instance.")
            self.recommendations.append("Use Azure Database Migration Service for minimal downtime migration.")
            self.readiness_score += 15

    def _check_web_server_migration(self, data):
        """Check web server migration requirements"""
        iis_info = data.get('IISInfo', {})

        if iis_info.get('Installed'):
            sites = iis_info.get('Sites', [])
            if sites:
                self.recommendations.append(f"IIS detected with {len(sites)} site(s). Consider Azure App Service migration.")
                self.recommendations.append("Use App Service Migration Assistant for assessment.")
                self.readiness_score += 15

    def _generate_report(self, data):
        """Generate migration readiness report"""
        # Calculate final readiness score (0-100)
        final_score = max(0, min(100, self.readiness_score))

        if final_score >= 80:
            readiness_level = "High"
            migration_approach = "Recommended for immediate migration"
        elif final_score >= 60:
            readiness_level = "Medium"
            migration_approach = "Ready with minor preparations"
        elif final_score >= 40:
            readiness_level = "Low"
            migration_approach = "Requires preparation before migration"
        else:
            readiness_level = "Not Ready"
            migration_approach = "Significant preparation required"

        report = {
            'server_name': data.get('SystemInfo', {}).get('ServerName', 'Unknown'),
            'assessment_date': datetime.now().strftime('%Y-%m-%d %H:%M:%S'),
            'readiness_score': final_score,
            'readiness_level': readiness_level,
            'migration_approach': migration_approach,
            'blocking_issues': self.blocking_issues,
            'warnings': self.warnings,
            'recommendations': self.recommendations
        }

        return report

def main():
    if len(sys.argv) != 2:
        print("Usage: python check-migration-readiness.py <assessment_file.json>")
        sys.exit(1)

    assessment_file = sys.argv[1]
    if not os.path.exists(assessment_file):
        print(f"Error: Assessment file '{assessment_file}' not found.")
        sys.exit(1)

    checker = MigrationReadinessChecker()
    report = checker.analyze_assessment(assessment_file)

    if isinstance(report, str):
        print(report)
        sys.exit(1)

    # Print report
    print(f"\n=== Migration Readiness Report for {report['server_name']} ===")
    print(f"Assessment Date: {report['assessment_date']}")
    print(f"Readiness Score: {report['readiness_score']}/100")
    print(f"Readiness Level: {report['readiness_level']}")
    print(f"Migration Approach: {report['migration_approach']}")

    if report['blocking_issues']:
        print("\nüö´ BLOCKING ISSUES:")
        for issue in report['blocking_issues']:
            print(f"   - {issue}")

    if report['warnings']:
        print("\n‚ö†Ô∏è  WARNINGS:")
        for warning in report['warnings']:
            print(f"   - {warning}")

    if report['recommendations']:
        print("\nüí° RECOMMENDATIONS:")
        for recommendation in report['recommendations']:
            print(f"   - {recommendation}")

    # Save report to file
    output_file = f"{report['server_name']}-readiness-report.json"
    with open(output_file, 'w') as f:
        json.dump(report, f, indent=2)

    print(f"\nDetailed report saved to: {output_file}")

if __name__ == "__main__":
    main()
EOF

chmod +x check-migration-readiness.py

echo "Migration assessment tools created successfully."
echo "\nUsage Instructions:"
echo "1. Run migration-assessment-script.ps1 on Windows servers"
echo "2. Analyze results with: python3 check-migration-readiness.py <assessment-file>.json"
